sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	//Call Script Include
	var getCasesFor = new GlideAjax('fetchCasesForUser');
	//give parameters to Script Include
	getCasesFor.addParam('sysparm_name', 'getCases');
	getCasesFor.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
	getCasesFor.getXML(populatedesc);

   //the response from fetchCasesForUser
   function populatedesc(response){
	   var casesFromSI = response.responseXML.documentElement.getAttribute("answer");
	   //remove ugly commas from array
	   casesFromSI = casesFromSI.replace(",", "");
	   var oldDesc = g_form.getValue('description');
	   //remove erlier cases from description if found
	   var pos = oldDesc.search("Cases");
	   if (pos != -1){
		   //-1 to remove old newlines from earlier setValue
		   oldDesc = oldDesc.slice(0, pos-1);
	   }
// 	   g_form.addInfoMessage("Pos: " + pos);
// 	   g_form.addInfoMessage("oldDesc: "+ oldDesc);
	   g_form.setValue('description', oldDesc + "\n" + "Cases: \n" + casesFromSI);
   }
}
