var fetchCasesForUser = Class.create();
fetchCasesForUser.prototype = Object.extendsObject(global.AbstractAjaxProcessor,{

	getCases: function(){
		var userObj = new GlideRecord('x_58872_needit_needit');
		//gs.info(test);
		userObj.get('u_requested_for', this.getParameter('sysparm_userID'));
		var holder = [];
		while(userObj.next()){
			holder.push(userObj.number + "\n");

		}
		gs.info("fetchCasesForUser returning: " + holder.toString());
		return holder.toString();
	},
	type: 'fetchCasesForUser'

});
